"use strict";var map,mapDiv=document.getElementById("officeMap"),mapData=mapDiv.dataset,center={lat:Number(mapData.lat),lng:Number(mapData.lng)};function initMap(){map=new google.maps.Map(mapDiv,{center:center,zoom:16});new google.maps.Marker({position:center,map:map,icon:"../img/icons/location-pin.svg"});google.maps.event.addDomListener(window,"resize",function(){var e=document.documentElement.clientWidth;setTimeout(function(){map.setCenter(center),e<768?map.setZoom(15):map.setZoom(16)},500)})}